.book-container {
  --height: 550px;
  --width: 420px;
  --rotate: -27deg;
  --radii: 5px;
}

.book-container {
  width: var(--width);
  height: var(--height);
  margin: 80px 30px -80px;
  display: flex;
  align-items: center;
  justify-content: center;
  perspective: 800px;
}

.book {
  transform: rotateY(var(--rotate));
  position: relative;
  transform-style: preserve-3d;
  width: var(--width);
  height: var(--height);
  transition: transform 1s ease;
  animation: 1s ease 0s 1 initAnimation;
}

.book:hover {
  transform: rotate(0deg);
}

@keyframes initAnimation {
  0% {
    transform: rotateY(5deg);
  }
  100% {
    transform: rotateY(var(--rotate));
  }
}
.book > :first-child {
  position: absolute;
  background: #0d47a1aa;
  width: var(--width);
  height: var(--height);
  border-top-right-radius: var(--radii);
  border-bottom-right-radius: var(--radii);
  box-shadow: 5px 5px 10px rgba(0, 0, 0, 0.35);
}

.book::before {
  content: " ";
  background: #fff;
  height: calc(var(--height) - 2 * 3px);
  width: 50px;
  top: 3px;
  position: absolute;
  transform: translateX(calc(var(--width) - 50px / 2 - 3px)) rotateY(90deg) translateX(calc(50px / 2));
}

.book::after {
  content: " ";
  position: absolute;
  left: 0;
  width: var(--width);
  height: var(--height);
  border-top-right-radius: var(--radii);
  border-bottom-right-radius: var(--radii);
  background: #01060f;
  transform: translateZ(-50px);
  box-shadow: -10px 0 50px 10px rgba(0, 0, 0, 0.5);
}

body {
  font-family: proxima-nova, -apple-system, BlinkMacSystemFont, "Segoe UI", "Roboto", "Oxygen", "Ubuntu", "Cantarell", "Fira Sans", "Droid Sans", "Helvetica Neue", sans-serif;
  margin: 0;
  padding: 0;
  background: #fff;
  color: #2b2b2b;
  line-height: 1.75;
  font-size: 1.1em;
}

@keyframes smoothscroll1 {
  from, to {
    scroll-behavior: smooth;
  }
}
@keyframes smoothscroll2 {
  from, to {
    scroll-behavior: smooth;
  }
}
html {
  animation: smoothscroll1 1s;
}

html:focus-within {
  animation-name: smoothscroll2;
  scroll-behavior: smooth;
}

a {
  color: inherit;
}
.about a:focus {
  background: #45576b;
  color: #fff;
  padding: 0.1em 0.1em;
  margin: -0.1em -0.1em;
  outline-color: transparent;
}

.wrapper {
  max-width: 100%;
  width: 1024px;
  margin: 0 auto;
}
.wrapper--stage {
  display: grid;
  grid-template-columns: 1fr 1fr;
  color: #45576b;
}
.wrapper--stage article {
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.btn {
  background: #45576b;
  color: #fff;
  text-decoration: none;
  padding: 1rem 1.25rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
}
.btn:hover, .btn:focus {
  text-decoration: underline;
  text-underline-offset: 4px;
  outline-color: transparent;
}
.btn small {
  font-size: 0.66em;
  font-style: italic;
}

.btn--sample {
  background: #4d6c76;
}

.btn--cta {
  font-weight: 800;
  text-transform: uppercase;
