  display: inline-block;
  color: #45576b;
}
.toc .o-list--bare > li {
  width: 100%;
  font-weight: 700;
}
.o-list--bare > li .inner-wrapper {
  display: grid;
  grid-template-columns: 4fr 4fr 1fr;
