.buy .link:focus {
  background: #45576b;
  color: #fff;
  padding: 0.5em 1em;
  margin: -0.5em -1em;
  outline-color: transparent;
}
.buy .link::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
}

